import React, { useState, useRef, useEffect } from "react";
import * as d3 from "d3";

const HappinessChart = ({ data }) => {
  const [sortCriteria, setSortCriteria] = useState("score");
  const [searchTerm, setSearchTerm] = useState("");
  const chartRef = useRef();
  const observerRef = useRef(null);

  const handleSortChange = (event) => {
    setSortCriteria(event.target.value);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value.toLowerCase());
  };

  const filteredData = data
    .filter((d) => d["Country or region"].toLowerCase().includes(searchTerm))
    .sort((a, b) => {
      if (sortCriteria === "alphabetical") {
        return a["Country or region"].localeCompare(b["Country or region"]);
      } else if (sortCriteria === "score") {
        return b["Score"] - a["Score"];
      }
      return 0;
    });

  useEffect(() => {
    if (filteredData.length === 0) return;

    // Clear any previous SVG elements
    d3.select(chartRef.current).selectAll("*").remove();

    // Set up the SVG element and its dimensions
    const svg = d3
      .select(chartRef.current)
      .attr("width", window.innerWidth)
      .attr("height", filteredData.length * 50 + 150) // Extra space for legend
      .style("overflow", "visible");

    // Add a legend at the top
    const legend = svg
      .append("g")
      .attr("class", "legend")
      .attr(
        "transform",
        `translate(${(window.innerWidth - 200) / 2 - 150}, 10)`
      );

    const legendData = [
      { color: "#00ff00", text: "High Happiness" },
      { color: "#ffff00", text: "Medium Happiness" },
      { color: "red", text: "Low Happiness" },
    ];

    const legendItemWidth = 120; // Adjust this value as needed
    const legendSpacing = 20; // Adjust this value as needed

    legend
      .selectAll("rect")
      .data(legendData)
      .enter()
      .append("rect")
      .attr(
        "transform",
        (d, i) => `translate(${i * (legendItemWidth + legendSpacing)}, 0)`
      )
      .attr("width", 20)
      .attr("height", 20)
      .attr("fill", (d) => d.color);

    legend
      .selectAll("text")
      .data(legendData)
      .enter()
      .append("text")
      .attr(
        "transform",
        (d, i) => `translate(${i * (legendItemWidth + legendSpacing) + 30}, 15)`
      )
      .text((d) => d.text)
      .attr("font-size", "12px")
      .attr("fill", "black");

    // Set up the scales
    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(filteredData, (d) => +d["Score"])])
      .range([0, window.innerWidth - 200]);

    // Set up the color scale
    const colorScale = d3
      .scaleSequential()
      .domain([
        d3.min(filteredData, (d) => +d["Score"]),
        d3.max(filteredData, (d) => +d["Score"]),
      ])
      .interpolator(
        d3.interpolateRgbBasis([
          "#8b0000", // dark red
          "#ff4500", // orange-red
          "#ffd700", // yellow-orange
          "#ffff00", // yellow
          "#9acd32", // yellow-green
          "#00ff00", // green
        ])
      );

    // Create a custom tooltip
    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0)
      .style("position", "absolute")
      .style("background-color", "white")
      .style("border", "1px solid black")
      .style("padding", "10px")
      .style("border-radius", "5px")
      .style("pointer-events", "none");

    // Create the bars with tooltips and smooth scrolling
    const bars = svg
      .selectAll(".bar")
      .data(filteredData, (d) => d["Country or region"]) // Use key function to track data
      .join(
        (enter) =>
          enter
            .append("rect")
            .attr("class", "bar")
            .attr("y", (d, i) => i * 50 + 50)
            .attr("height", 40)
            .attr("fill", (d) => colorScale(d["Score"]))
            .attr(
              "id",
              (d) => `bar-${d["Country or region"].replace(/\s+/g, "-")}`
            )
            .attr("width", 0),
        (update) =>
          update
            .attr("y", (d, i) => i * 50 + 50)
            .attr("fill", (d) => colorScale(d["Score"])),
        (exit) => exit.transition().duration(500).attr("width", 0).remove()
      );

    // Add country labels inside each bar
    const labels = svg
      .selectAll(".label")
      .data(filteredData, (d) => d["Country or region"]) // Use key function to track data
      .join(
        (enter) =>
          enter
            .append("text")
            .attr("class", "label")
            .attr("y", (d, i) => i * 50 + 75)
            .attr("x", 10)
            .text((d) => d["Country or region"])
            .attr("font-size", "12px")
            .attr("fill", "black")
            .attr("opacity", 0),
        (update) =>
          update
            .attr("y", (d, i) => i * 50 + 75)
            .text((d) => d["Country or region"]),
        (exit) => exit.transition().duration(500).attr("opacity", 0).remove()
      );

    // Add score text to the right end of each bar inside the bar
    const scores = svg
      .selectAll(".score")
      .data(filteredData)
      .join(
        (enter) =>
          enter
            .append("text")
            .attr("class", "score")
            .attr("x", (d) => xScale(d["Score"]) - 5) // Position inside the bar, adjust as needed
            .attr("y", (d, i) => i * 50 + 75)
            .text((d) => d["Score"])
            .attr("font-size", "12px")
            .attr("fill", "black")
            .attr("text-anchor", "end")
            .attr("opacity", 0),
        (update) =>
          update.attr("y", (d, i) => i * 50 + 75).text((d) => d["Score"]),
        (exit) => exit.transition().duration(500).attr("opacity", 0).remove()
      );

    // Create Intersection Observer
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const bar = d3.select(entry.target);
            bar
              .transition()
              .duration(1000)
              .attr("width", (d) => xScale(d["Score"]));
            const scoreText = svg
              .selectAll(".score")
              .filter(
                (d) =>
                  `bar-${d["Country or region"].replace(/\s+/g, "-")}` ===
                  entry.target.id
              );
            scoreText
              .transition()
              .duration(1000)
              .attr("opacity", 1)
              .attr("x", (d) => xScale(d["Score"]) - 10); // Adjust padding
            const labelText = svg
              .selectAll(".label")
              .filter(
                (d) =>
                  `bar-${d["Country or region"].replace(/\s+/g, "-")}` ===
                  entry.target.id
              );
            labelText.transition().duration(1000).attr("opacity", 1);
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );

    bars.each(function () {
      observer.observe(this);
    });

    bars
      .on("mouseover", function (event, d) {
        tooltip.transition().duration(200).style("opacity", 0.9);
        tooltip
          .html(
            `<strong>Country:</strong> <span style='color:red'>${d["Country or region"]}</span><br>
            <strong>Score:</strong> <span style='color:red'>${d["Score"]}</span><br>`
          )
          .style("left", event.pageX + 10 + "px")
          .style("top", event.pageY - 28 + "px");
      })
      .on("mouseout", function () {
        tooltip.transition().duration(500).style("opacity", 0);
      })
      .on("click", function (event, d) {
        document
          .getElementById(`bar-${d["Country or region"].replace(/\s+/g, "-")}`)
          .scrollIntoView({ behavior: "smooth" });
      });

    return () => {
      tooltip.remove();
      if (observer) {
        bars.each(function () {
          observer.unobserve(this);
        });
      }
    };
  }, [filteredData]);

  return (
    <div>
      <div className="controls">
        <label>
          Sort by:
          <select value={sortCriteria} onChange={handleSortChange}>
            <option value="score">Score</option>
            <option value="alphabetical">Alphabetical</option>
          </select>
        </label>
        <label>
          Search:
          <input type="text" value={searchTerm} onChange={handleSearchChange} />
        </label>
      </div>
      <svg ref={chartRef}></svg>
    </div>
  );
};

export default HappinessChart;
