import React, { useRef, useEffect } from "react";
import * as d3 from "d3";
import d3Tip from "d3-tip";

const HappinessChart = ({ data }) => {
  const chartRef = useRef();

  useEffect(() => {
    if (data.length === 0) return;

    // Clear any previous SVG elements
    d3.select(chartRef.current).selectAll("*").remove();

    // Set up the SVG element and its dimensions
    const svg = d3
      .select(chartRef.current)
      .attr("width", 1500)
      .attr("height", 600)
      .style("overflow", "visible")
      .style("margin-top", "75px");

    // Set up the scales and axes
    const xScale = d3
      .scaleBand()
      .domain(data.map((d) => d["Country or region"]))
      .range([0, 1500])
      .padding(0.1);

    const yScale = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => +d["Score"])])
      .range([400, 0]);

    const xAxis = d3.axisBottom(xScale);
    const yAxis = d3.axisLeft(yScale);

    svg
      .append("g")
      .call(xAxis)
      .attr("transform", "translate(0, 400)")
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .attr("text-anchor", "end")
      .attr("dx", "-0.8em")
      .attr("dy", "0.15em")
      .style("font-size", "12px");

    svg.append("g").call(yAxis);

    // Create the tooltip
    const tip = d3Tip()
      .attr("class", "d3-tip")
      .offset([-10, 0])
      .html(
        (
          d
        ) => `<strong>Country:</strong> <span style='color:red'>${d["Country or region"]}</span><br>
                  <strong>Score:</strong> <span style='color:red'>${d["Score"]}</span><br>
                  <strong>GDP per capita:</strong> <span style='color:red'>${d[" GDP per capita"]}</span><br>
                  <strong>Social support:</strong> <span style='color:red'>${d[" Social support"]}</span><br>
                  <strong>Healthy life expectancy:</strong> <span style='color:red'>${d[" Healthy life expectancy"]}</span><br>
                  <strong>Freedom to make life choices:</strong> <span style='color:red'>${d["Freedom to make life choices"]}</span><br>
                  <strong>Generosity:</strong> <span style='color:red'>${d["Generosity"]}</span><br>
                  <strong>Perceptions of corruption:</strong> <span style='color:red'>${d["Perceptions of corruption"]}</span><br>`
      );

    svg.call(tip);

    // Create the bars with tooltips
    svg
      .selectAll(".bar")
      .data(data)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", (d) => xScale(d["Country or region"]))
      .attr("y", (d) => yScale(d["Score"]))
      .attr("width", xScale.bandwidth())
      .attr("height", (d) => 400 - yScale(d["Score"]))
      .attr("fill", "steelblue")
      .on("mouseover", tip.show)
      .on("mouseout", tip.hide);
  }, [data]);

  return <svg ref={chartRef}></svg>;
};

export default HappinessChart;
