import React, { useState, useRef, useEffect } from "react";
import * as d3 from "d3";

const HappinessChart = ({ data }) => {
  const [sortCriteria, setSortCriteria] = useState("score");
  const [searchTerm, setSearchTerm] = useState("");
  const chartRef = useRef();

  const handleSortChange = (event) => {
    setSortCriteria(event.target.value);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value.toLowerCase());
  };

  const filteredData = data
    .filter((d) => d["Country or region"].toLowerCase().includes(searchTerm))
    .sort((a, b) => {
      if (sortCriteria === "alphabetical") {
        return a["Country or region"].localeCompare(b["Country or region"]);
      } else if (sortCriteria === "score") {
        return b["Score"] - a["Score"];
      }
      return 0;
    });

  useEffect(() => {
    if (filteredData.length === 0) return;

    // Clear any previous SVG elements
    d3.select(chartRef.current).selectAll("*").remove();

    // Set up the SVG element and its dimensions
    const svg = d3
      .select(chartRef.current)
      .attr("width", window.innerWidth)
      .attr("height", filteredData.length * 50)
      .style("overflow", "visible");

    // Set up the scales
    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(filteredData, (d) => +d["Score"])])
      .range([0, window.innerWidth - 200]);

    // Set up the color scale
    const colorScale = d3
      .scaleSequential()
      .domain([
        d3.min(filteredData, (d) => +d["Score"]),
        d3.max(filteredData, (d) => +d["Score"]),
      ])
      .interpolator(
        d3.interpolateRgbBasis([
          "#8b0000", // dark red
          "#ff4500", // orange-red
          "#ffd700", // yellow-orange
          "#ffff00", // yellow
          "#9acd32", // yellow-green
          "#00ff00", // green
        ])
      );

    // Create a custom tooltip
    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    // Create the bars with tooltips and smooth scrolling
    const bars = svg
      .selectAll(".bar")
      .data(filteredData, (d) => d["Country or region"]) // Use key function to track data
      .join(
        (enter) =>
          enter
            .append("rect")
            .attr("class", "bar")
            .attr("y", (d, i) => i * 50)
            .attr("height", 40)
            .attr("fill", (d) => colorScale(d["Score"]))
            .attr(
              "id",
              (d) => `bar-${d["Country or region"].replace(/\s+/g, "-")}`
            )
            .attr("width", 0)
            .call((enter) =>
              enter
                .transition()
                .duration(1000)
                .attr("width", (d) => xScale(d["Score"]))
            ),
        (update) =>
          update.call((update) =>
            update
              .transition()
              .duration(1000)
              .attr("width", (d) => xScale(d["Score"]))
              .attr("y", (d, i) => i * 50)
          ),
        (exit) =>
          exit.call((exit) =>
            exit.transition().duration(500).attr("width", 0).remove()
          )
      );

    bars
      .on("mouseover", function (event, d) {
        tooltip.transition().duration(200).style("opacity", 0.9);
        tooltip
          .html(
            "<strong>Country:</strong> <span style='color:red'>" +
              d["Country or region"] +
              "</span><br>" +
              "<strong>Score:</strong> <span style='color:red'>" +
              d["Score"] +
              "</span><br>"
          )
          .style("left", event.pageX + 10 + "px")
          .style("top", event.pageY - 28 + "px");
      })
      .on("mouseout", function () {
        tooltip.transition().duration(500).style("opacity", 0);
      })
      .on("click", function (event, d) {
        document
          .getElementById(`bar-${d["Country or region"].replace(/\s+/g, "-")}`)
          .scrollIntoView({ behavior: "smooth" });
      });

    // Create country labels with smooth scrolling
    const labels = svg
      .selectAll(".label")
      .data(filteredData, (d) => d["Country or region"]) // Use key function to track data
      .join(
        (enter) =>
          enter
            .append("text")
            .attr("class", "label")
            .attr("y", (d, i) => i * 50 + 25)
            .attr("x", 10)
            .text((d) => d["Country or region"])
            .attr("font-size", "12px")
            .attr("fill", "black")
            .attr(
              "id",
              (d) => `label-${d["Country or region"].replace(/\s+/g, "-")}`
            )
            .attr("opacity", 0)
            .call((enter) =>
              enter.transition().duration(1000).attr("opacity", 1)
            ),
        (update) =>
          update.call((update) =>
            update
              .transition()
              .duration(1000)
              .attr("y", (d, i) => i * 50 + 25)
          ),
        (exit) =>
          exit.call((exit) =>
            exit.transition().duration(500).attr("opacity", 0).remove()
          )
      );

    labels.on("click", function (event, d) {
      document
        .getElementById(`bar-${d["Country or region"].replace(/\s+/g, "-")}`)
        .scrollIntoView({ behavior: "smooth" });
    });
  }, [filteredData]);

  return (
    <div>
      <div>
        <label htmlFor="sort">Sort by: </label>
        <select id="sort" onChange={handleSortChange} value={sortCriteria}>
          <option value="score">Happiness Score</option>
          <option value="alphabetical">Alphabetical</option>
        </select>

        <label htmlFor="search">Search: </label>
        <input
          id="search"
          type="text"
          placeholder="Search country"
          onChange={handleSearchChange}
          value={searchTerm}
        />
      </div>
      <svg ref={chartRef}></svg>
    </div>
  );
};

export default HappinessChart;
