import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

const HappinessChart = ({ data }) => {
  const chartRef = useRef();

  useEffect(() => {
    if (data.length === 0) return;

    // Clear any previous SVG elements
    d3.select(chartRef.current).selectAll("*").remove();

    // Set up the SVG element and its dimensions
    const svg = d3
      .select(chartRef.current)
      .attr("width", window.innerWidth)
      .attr("height", data.length * 50)
      .style("overflow", "visible");

    // Set up the scales
    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => +d["Score"])])
      .range([0, window.innerWidth - 200]);

    // Set up the color scale
    const colorScale = d3
      .scaleSequential()
      .domain([
        d3.min(data, (d) => +d["Score"]),
        d3.max(data, (d) => +d["Score"]),
      ])
      .interpolator(
        d3.interpolateRgbBasis([
          "#8b0000", // dark red

          "#00ff00", // green
          "#9acd32", // yellow-green
          "#ffff00", // yellow
          "#ffd700", // yellow-orange
          "#ff4500", // orange-red
          "#8b0000", // dark red
        ])
      );
    // Create a custom tooltip
    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    // Create the bars with tooltips
    svg
      .selectAll(".bar")
      .data(data)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("y", (d, i) => i * 50)
      .attr("width", 0)
      .attr("height", 40)
      .attr("fill", (d) => colorScale(d["Score"]))
      .on("mouseover", function (event, d) {
        tooltip.transition().duration(200).style("opacity", 0.9);
        tooltip
          .html(
            "<strong>Country:</strong> <span style='color:red'>" +
              d["Country or region"] +
              "</span><br>" +
              "<strong>Score:</strong> <span style='color:red'>" +
              d["Score"] +
              "</span><br>"
          )
          .style("left", event.pageX + 10 + "px")
          .style("top", event.pageY - 28 + "px");
      })
      .on("mouseout", function (d) {
        tooltip.transition().duration(500).style("opacity", 0);
      })
      .transition()
      .duration(1000)
      .attr("width", (d) => xScale(d["Score"]))
      .delay((d, i) => i * 50);

    // Create country labels
    svg
      .selectAll(".label")
      .data(data)
      .enter()
      .append("text")
      .attr("class", "label")
      .attr("y", (d, i) => i * 50 + 25)
      .attr("x", 10)
      .text((d) => d["Country or region"])
      .attr("font-size", "12px")
      .attr("fill", "black");
  }, [data]);

  return <svg ref={chartRef}></svg>;
};

export default HappinessChart;
